create database blog;

use blog;

SET FOREIGN_KEY_CHECKS=0;

-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(11) NOT NULL auto_increment,
  `author` varchar(255) default NULL COMMENT '作者',
  `title` varchar(255) default NULL COMMENT '标题',
  `content` longtext COMMENT '内容',
  `keyWords` varchar(255) default NULL COMMENT '关键字',
  `description` varchar(255) default NULL COMMENT '描述',
  `categoryId` int(11) default NULL COMMENT '栏目',
  `label` varchar(255) default NULL COMMENT '标签，用逗号分隔',
  `titleImgs` varchar(255) default NULL COMMENT '标题图片列表',
  `status` varchar(255) default NULL COMMENT '状态',
  `createTime` timestamp NULL default CURRENT_TIMESTAMP COMMENT '发布时间',
  `readCnt` int(11) default NULL COMMENT '阅读量',
  `agreeCnt` int(11) default NULL COMMENT '点赞数',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

