-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `id` int(11) NOT NULL auto_increment,
  `articleId` int(11) default NULL COMMENT '文章Id',
  `content` varchar(2000) default NULL COMMENT '评论内容',
  `createBy` int(11) default NULL COMMENT '评论人Id',
  `createTime` timestamp NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '评论时间',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

